fpsLabel.Font = Enum.Font.SourceSansBold
fpsLabel.Text = "FPS: 0"
fpsLabel.Parent = main

local last = tick()
local frames = 0
RunService.RenderStepped:Connect(function()
    frames = frames + 1
    local now = tick()
    if now - last >= 1 then
        fpsLabel.Text = "FPS: " .. frames
        frames = 0
        last = now
    end
end)
