-- Button creator
local function MakeBtn(txt, y)
    local b = Instance.new("TextButton", main)
    b.Size = UDim2.new(0,260,0,35)
    b.Position = UDim2.new(0,20,0,y)
    b.BackgroundColor3 = Color3.fromRGB(40,40,40)
    b.TextColor3 = Color3.new(1,1,1)
    b.Text = txt
    return b
end

-- Abilities
MakeBtn("Velocidad x4", 50).MouseButton1Click:Connect(function() hum.WalkSpeed = 64 end)
MakeBtn("Salto alto", 90).MouseButton1Click:Connect(function() hum.JumpPower = 160 end)
MakeBtn("Godmode", 130).MouseButton1Click:Connect(function() hum.Health = math.huge end)

-- Noclip
do
    local noclip = false
    MakeBtn("Noclip", 170).MouseButton1Click:Connect(function()
        noclip = not noclip
        while noclip do
            task.wait()
            for _,v in ipairs(char:GetDescendants()) do
                if v:IsA("BasePart") then v.CanCollide = false end
            end
        end
    end)
end

-- Fly
MakeBtn("Fly", 210).MouseButton1Click:Connect(function()
    loadstring(game:HttpGet("https://pastebin.com/raw/Y9bC8RjL"))()
end)

-- Invisibilidad
MakeBtn("Invisibilidad", 250).MouseButton1Click:Connect(function()
    if char:FindFirstChild("Head") then char.Head.Transparency = 1 end
    for _,v in ipairs(char:GetDescendants()) do
        if v:IsA("BasePart") then v.Transparency = 1 end
    end
end)

-- Dash
MakeBtn("Dash", 290).MouseButton1Click:Connect(function()
